var minimumCost = function(m, n, horizontalCuts, verticalCuts) {
    let cost = 0;

    horizontalCuts.sort((a, b) => b - a); 
    verticalCuts.sort((a, b) => b - a);  

    let horizontalCount = 1; 
    let verticalCount = 1;  
    let hIndex = 0;         
    let vIndex = 0;        

    while (hIndex < horizontalCuts.length && vIndex < verticalCuts.length) {
        if (horizontalCuts[hIndex] > verticalCuts[vIndex]) {
            cost += horizontalCuts[hIndex] * verticalCount;
            horizontalCount++;
            hIndex++;
        } else {
            cost += verticalCuts[vIndex] * horizontalCount;
            verticalCount++;
            vIndex++;
        }
    }


    let totalHorizontal = 0;
    while (hIndex < horizontalCuts.length) {
        totalHorizontal += horizontalCuts[hIndex];
        hIndex++;
    }
    cost += totalHorizontal * verticalCount;


    let totalVertical = 0;
    while (vIndex < verticalCuts.length) {
        totalVertical += verticalCuts[vIndex];
        vIndex++;
    }
    cost += totalVertical * horizontalCount;

    return Math.floor(cost);
};
