class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        sort(hor.begin() , hor.end() , greater<int>());
        sort(ver.begin() , ver.end() , greater<int>());
        int temp = 0;
        int i = 0 , j = 0 , c1 = 1 , c2 = 1;
        long long c = 0;
        while(i < hor.size() && j < ver.size()){
            if(hor[i] > ver[j]){
                c += (hor[i]*c2);
                c1++;
                i++;
            }
            else{
                c += (ver[j]*c1);
                c2++;
                j++;
            }
        }
        while(i < hor.size()){
            c += (hor[i]*c2);
            i++;
        }
        while(j < ver.size()){
            c += (ver[j]*c1);
            j++;
        }
        return c;
    }
};
