class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(), horizontalCut.end());
        sort(verticalCut.begin(), verticalCut.end());
        int f1 = horizontalCut.size()-1;
        int f2 = verticalCut.size()-1;
        int c1 = 1;
        int c2 = 1;
        long long res = 0;
        while(f1>=0 || f2>=0){
            if(f1 < 0){
                res = res + c2 * verticalCut[f2];
                f2--;
                c1++;
            }
            else if(f2 < 0){
                res = res + c1 * horizontalCut[f1];
                f1--;
                c2++;
            }
            else{
                if(horizontalCut[f1] > verticalCut[f2]){
                    res = res + c1 * horizontalCut[f1];
                    f1--;
                    c2++;
                }
                else{
                    res = res + c2 * verticalCut[f2];
                    f2--;
                    c1++;
                }
            }
        }
        return res;
    }
};
