class Tri{
    int a,b,c;
    Tri(int a,int b,int c)
    {
        this.a=a;
        this.b=b;
        this.c=c;
    }
}
class Solution {
    public long minimumCost(int m, int n, int[] hc, int[] vc) {
        long an=0;
        int cv=0;
        int ch=0;
        PriorityQueue<Tri>pq=new PriorityQueue<>((d,e)->d.c-e.c);
        for(int i=0;i<m-1;i++)
        pq.add(new Tri(1,i,-hc[i]));
        for(int i=0;i<n-1;i++)
        pq.add(new Tri(2,i,-vc[i]));
        while(!pq.isEmpty())
        {
            Tri z=pq.poll();
            int c=z.c;
            c=c*-1;
            if(z.a==1)
            {
                ch++;
                an+=(c*(cv+1));
            }
            else
            {
                cv++;
                an+=(c*(ch+1));
            }
        }
        return an;
    }
}
