class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long res = 0;
        priority_queue<pair<long long, long long>> pq;
        for (auto& h : horizontalCut) pq.push({h, 0});
        for (auto& v : verticalCut) pq.push({v, 1});

        long long hCnt = 0, vCnt = 0;
        while (!pq.empty()) {
            auto [price, type] = pq.top(); pq.pop();
            if (type == 0) {
                res += 1ll * price * (vCnt + 1);
                hCnt++;
            }
            else {
                res += 1ll * price * (hCnt + 1);
                vCnt++;
            }
        }

        return res;
    }
};
