class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        options = []
        for cut in horizontalCut:
            options.append((cut, 'H'))
        for cut in verticalCut:
            options.append((cut, 'V'))

        options.sort(key = lambda x: x[0], reverse = True)

        answer = 0
        horizontal_fragment = 1
        vertical_fragment = 1
        for option in options:
            if (option[1] == 'H'):
                answer += horizontal_fragment * option[0]
                vertical_fragment += 1
            else:
                answer += vertical_fragment * option[0]
                horizontal_fragment += 1

        return answer
