class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
        sort(verticalCut.begin(), verticalCut.end(), greater<int>());

        long long h = 0; // Horizontal cuts index
        long long v = 0; // Vertical cuts index
        long long horizontalPieces = 1; // Start with 1 piece horizontally
        long long verticalPieces = 1;   // Start with 1 piece vertically
        long long totalCost = 0;

        // While there are still cuts to be made
        while (h < horizontalCut.size() && v < verticalCut.size()) {
            if (horizontalCut[h] > verticalCut[v]) {
                totalCost += horizontalCut[h] * verticalPieces;
                horizontalPieces++;
                h++;
            } else {
                totalCost += verticalCut[v] * horizontalPieces;
                verticalPieces++;
                v++;
            }
        }

        // Add remaining horizontal cuts if any
        while (h < horizontalCut.size()) {
            totalCost += horizontalCut[h] * verticalPieces;
            h++;
        }

        // Add remaining vertical cuts if any
        while (v < verticalCut.size()) {
            totalCost += verticalCut[v] * horizontalPieces;
            v++;
        }

        return totalCost;
    }
};
