#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        int nh = hc.size();
        int nv = vc.size();
        vector<pair<ll,char>>v;
        for(int i = 0;i<nh;i++){
            v.push_back({hc[i], 'H'});
        }
        for(int i = 0;i<nv;i++){
            v.push_back({vc[i], 'V'});
        }
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());
        ll h = 0, vt = 0;
        int long long ans = 0;
        for(int i = 0;i<v.size();i++){
            if(v[i].second == 'H'){
                ans += (v[i].first * (vt+1));
                h++;
            }else{
                ans += (v[i].first * (h+1));
                vt++;
            }
        }
        return ans;
    }
};
