class Solution {
public:
    long long minimumCost(int rows, int cols, vector<int>& horizCuts,
                          vector<int>& vertCuts) {
        sort(horizCuts.rbegin(), horizCuts.rend());
        sort(vertCuts.rbegin(), vertCuts.rend());

        int hIndex = 0, vIndex = 0;
        int horizPieces = 1, vertPieces = 1;
        long long totalCost = 0;

        while (hIndex < horizCuts.size() && vIndex < vertCuts.size()) {
            if (horizCuts[hIndex] >= vertCuts[vIndex]) {
                totalCost += horizCuts[hIndex] * vertPieces;
                horizPieces++;
                hIndex++;
            } else {
                totalCost += vertCuts[vIndex] * horizPieces;
                vertPieces++;
                vIndex++;
            }
        }

        while (hIndex < horizCuts.size()) {
            totalCost += horizCuts[hIndex] * vertPieces;
            horizPieces++;
            hIndex++;
        }

        while (vIndex < vertCuts.size()) {
            totalCost += vertCuts[vIndex] * horizPieces;
            vertPieces++;
            vIndex++;
        }

        return totalCost;
    }
};
