class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         vector<pair<int, char>> cuts;
        for (int c : horizontalCut){
            cuts.push_back(make_pair(c, 'H'));
        }
        for (int c : verticalCut){
            cuts.push_back(make_pair(c, 'V'));
        } 
        sort(cuts.rbegin(), cuts.rend());
        long long ans = 0, hSeg = 1, vSeg = 1;
        for (auto cut : cuts) {
            int c = cut.first;
            char t = cut.second;
            if (t == 'H') {
                ans += c * vSeg;
                hSeg++;
            } else {
                ans += c * hSeg;
                vSeg++;
            }
        }
        return ans;
    }
};
