class Solution {
public:
    static bool compare(pair<int,int>&p1,pair<int,int>&p2){
        return p1.first>p2.first;
    }
    long long minimumCost(int m, int n, vector<int>& hC, vector<int>& vC) {
        vector<pair<int, int>> temp;
        for (int i = 0; i < m - 1; ++i) {
            temp.push_back({hC[i],0});
        }
        for (int j = 0; j < n - 1; ++j) {
            temp.push_back({vC[j],1});
        }
        sort(temp.begin(), temp.end(),compare);

        int hP = 1;
        int vP = 1;
        long long res = 0;
        for (auto ele : temp) {
            if (ele.second ==0) {
                res +=ele.first * vP;
                hP++;
            } else {
                res +=ele.first * hP;
                vP++;
            }
        }

        return res;
    }
};
