class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,bool>> cuts;
        for(auto i : horizontalCut){
            cuts.push_back({i,true});
        }
        for(auto i : verticalCut){
            cuts.push_back({i,false});
        }
        sort(cuts.begin(),cuts.end() , greater<pair<int,bool>>());
        int row = 1, col = 1;
        long long ans = 0;
        for(auto i : cuts){
            if(i.second){
                ans += i.first * col;
                row++;
            }else{
                ans += i.first * row;
                col++;
            }
        }
        return ans;
    }
};
