class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {


         vector<pair<int, char>> cuts;

        for (int i: h)
            cuts.push_back({i, 'H'});       
        for (int i : v)
            cuts.push_back({i, 'V'});

        sort(cuts.begin(), cuts.end());
        reverse(cuts.begin(), cuts.end());

        long long hs=1,vs=1,tc=0;

        for (auto i: cuts) 
        {
            if (i.second == 'H') 
            {
                tc += i.first * vs;
                hs++;
            } 
            else 
            {
                tc += i.first * hs;
                vs++;
            }
        }

        return tc;
    }
};
