class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& HC, vector<int>& VC) {
        sort(HC.rbegin(), HC.rend());
        sort(VC.rbegin(), VC.rend());

        int64_t v1 = 0, v2 = 0;
        int64_t hh = 0, vv = 0;
        int64_t ans = 0;
        while (hh < m - 1 && vv < n - 1) {
            if (HC[hh] >= VC[vv]) {
                ans += HC[hh] * (v2 + 1);
                v1++;
                hh++;
            } else {
                ans += VC[vv] * (v1 + 1);
                v2++;
                vv++;
            }
        }
        while (hh < m - 1) {
            ans += HC[hh] * (v2 + 1);
            hh++;
        }
        while (vv < n - 1) {
            ans += VC[vv] * (v1 + 1);
            vv++;
        }
        return ans;
    }
};
