class Solution {
public:

    long long solve(int m, int n, vector<int>& a, vector<int>& b){
        vector<pair<int, char>> arr;
        long long v = 1, h = 1,ans = 0;

        for(auto i: a) {
             arr.push_back({i, 'H'});
        }
        for(auto i: b) {
            arr.push_back({i, 'V'});
        }
        sort(arr.rbegin(), arr.rend());


        for(auto& i : arr) {
            if(i.second == 'H') {
                ans += i.first * v;
                h++;
            } else {
                ans += i.first * h;
                v++;
            }
        }

        return ans;
    }

    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        return solve(m,n,horizontalCut,verticalCut);
    }
};
