class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        int h=1, v=1;

        sort(hc.begin(), hc.end());
        sort(vc.begin(), vc.end());

        int i=m-2, j=n-2;
        long long ans =0;

        while(i>=0 && j>=0){
            if(hc[i] > vc[j]){
                ans += 1ll*hc[i]*v;
                h++;
                i--;
            }
            else{
                ans += 1ll*vc[j]*h;
                v++;
                j--;
            }
        }
        while(i>=0){
            ans += 1ll*hc[i]*v;
            h++;
            i--;
        }
        while(j>=0){
            ans += 1ll*vc[j]*h;
            v++;
            j--;
        }
        return ans;
    }
};
