class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizonal, vector<int>& vertical) {
        int h=1,v=1;
        sort(horizonal.rbegin(), horizonal.rend());
        sort(vertical.rbegin(), vertical.rend());

        int i=0,j=0;
        long long ans=0LL;
        while(i<(m-1) && j<(n-1)){
            if(horizonal[i] > vertical[j]){
                ans += horizonal[i]*v*1LL;
                h++;
                i++;
            }else{
                ans += vertical[j]*h*1LL;
                v++;
                j++;
            }
        }
        while(i<(m-1)){
            ans += horizonal[i++]*v*1LL;
        }
        while(j<(n-1)){
            ans += vertical[j++]*h*1LL;
        }
        return ans;
    }
};
