class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        vector<pair<int, bool>> cuts;

        for (int cost : hCut) {
            cuts.push_back({cost, true});
        }

        for (int cost : vCut) {
            cuts.push_back({cost, false});
        }

        sort(cuts.rbegin(), cuts.rend());

        long long totalCost = 0;
        long long hPcs = 1; 
        long long vPcs = 1; 


        for (auto& cut : cuts) {
            int cost = cut.first;
            bool isHorizontal = cut.second;

            if (isHorizontal) {
                totalCost += cost * vPcs;
                hPcs++;
            } else {
                totalCost += cost * hPcs;
                vPcs++;
            }
        }

        return totalCost;
    }
};
