class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue<pair<long long,long long>> pq;
        int r=1,c=1;
        for(auto it:h)
            pq.push({it,0});
        for(auto it:v)
            pq.push({it,1});
        long long ans=0,ans1=0;
        while(!pq.empty())
        {
            int x=pq.top().first;
            int y=pq.top().second;
            pq.pop();
            if(y==0)
                ans+=(x*c),++r;
            else
                ans+=(x*r),++c;
        }
        r=1,c=1;
        for(auto it:h)
            pq.push({it,1});
        for(auto it:v)
            pq.push({it,0});
        while(!pq.empty())
        {
            int x=pq.top().first;
            int y=pq.top().second;
            pq.pop();
            if(y==0)
                ans1+=(x*c),++r;
            else
                ans1+=(x*r),++c;
        }
        return min(ans,ans1);
    }
};
