class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        --m;
        --n;
        sort(hc.rbegin(), hc.rend());
        sort(vc.rbegin(), vc.rend());
        long long ans=0;
        long long hh=0, vv=0;
        long long hi=0, vi=0;
        while(hi<hc.size()||vi<vc.size()){
            long long hval=0, cval=0;
            long long ihc=0, ivc=0;
            if(hi<hc.size()){
                hval = hc[hi];
                ihc=(1l+vv)*hval;
            }
            if(vi<vc.size()){
                cval = vc[vi];
                ivc=(1l+hh)*cval;
            }
            // if(ihc>ivc){
            if(hval>cval){
                ans+=ihc;
                // cout<<"h with "<<ihc<<" < "<<ivc<<" ans = "<<ans<<endl;
                ++hi;
                ++hh;
            }else{
                ans+=ivc;
                // cout<<"v with "<<ivc<<" < "<<ihc<<" ans = "<<ans<<endl;
                ++vi;
                ++vv;
            }
        }
        return ans;
    }
};
