#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        sort(hCut.begin(), hCut.end(), greater<int>());
        sort(vCut.begin(), vCut.end(), greater<int>());

        int hSeg = 1;
        int vSeg = 1; 
        int i = 0, j = 0;
        ll tCost = 0;

        while (i < hCut.size() && j < vCut.size()) {
            if (hCut[i] >= vCut[j]) {
                tCost += hCut[i] * vSeg;
                hSeg++;
                i++;
            } else {
                tCost += vCut[j] * hSeg;
                vSeg++;
                j++;
            }
        }

        while (i < hCut.size()) {
            tCost += hCut[i] * vSeg;
            hSeg++;
            i++;
        }

        while (j < vCut.size()) {
            tCost += vCut[j] * hSeg;
            vSeg++;
            j++;
        }

        return tCost;
    }
};
