class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int, int>> A;
        for(auto x:horizontalCut) A.push_back({x, 0});
        for(auto x:verticalCut) A.push_back({x, 1});
        sort(begin(A), end(A));

        int h = 1, v = 1, sz = A.size();
        long long ans = 0;
        for(int i = sz-1; i>=0; i--){
            if(A[i].second==0) {
                ans += (A[i].first*h);
                v++;
            }else{
                ans += (A[i].first*v);
                h++;
            }
        }
        return ans;
    }
};
