#define ll long long


class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {


        sort(hc.begin(), hc.end());
        sort(vc.begin(), vc.end());

        int hcnt = 1;
        int vcnt = 1;

        ll ans = 0;


        while(!vc.empty() || !hc.empty())
        {
            ll vcurr = -1;
            ll hcurr = -1;

            if(!vc.empty())
            {   
                vcurr = vc.back();
            }

            if(!hc.empty())
            {
                hcurr = hc.back();
            }



            if(vcurr>=hcurr)
            {
                vc.pop_back();

                ans += vcurr*vcnt;
                ++hcnt;

            }

            else
            {
                hc.pop_back();

                ans += hcurr*hcnt;
                ++vcnt;

            }


        }


        return ans;



    }
};
