struct Cut {
int cost;
bool isHorizontal;
};
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
vector<Cut> cuts;


for (int i = 0; i < m - 1; ++i) {
cuts.push_back({horizontalCut[i], true});}
for (int j = 0; j < n - 1; ++j) {
cuts.push_back({verticalCut[j], false});
}

sort(cuts.begin(), cuts.end(), [](const Cut& a, const Cut& b) {
return a.cost > b.cost;
});

int hPieces = 1, vPieces = 1;
long long totalCost = 0;

for (const Cut& cut : cuts) {
if (cut.isHorizontal) {
totalCost += cut.cost * vPieces;
++hPieces;
} else {
totalCost += cut.cost * hPieces;
++vPieces;
}
}
return totalCost;

    }
};
