class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hcut, vcut = 0, 0
        horizontalCut.sort(reverse = True)
        verticalCut.sort(reverse = True)
        res = 0

        while hcut < m - 1 and vcut < n - 1:
            if horizontalCut[hcut] >= verticalCut[vcut]:
                res += horizontalCut[hcut] * (vcut + 1)
                hcut += 1
            else:
                res += verticalCut[vcut] * (hcut + 1)
                vcut += 1

        while hcut < m - 1:
            res += horizontalCut[hcut] * (vcut + 1)
            hcut += 1
        while vcut < n - 1:
            res += verticalCut[vcut] * (hcut + 1)
            vcut += 1
        return res
