struct DSU{
    int n;
    vi f, sz;
    DSU(int _n) {
        n = _n;
        f.resize(n);
        sz.resize(n);
        rep(i, 0, n) f[i] = i, sz[i] = 1;
    }
    int find(int a) {
        return f[a] == a ? a : f[a] = find(f[a]);
    }
    int merge(int a, int b) {
        int x = find(a), y = find(b);
        if (x == y) return 0;
        if (sz[x] < sz[y]) swap(x, y);
        sz[x] += sz[y];
        f[y] = x;
        return 1;
    }
};

