class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        vector<pii> e;
        rep(i, 0, m-1) e.pb({a[i], i});
        rep(i, 0, n-1) e.pb({b[i], i+m});
        sort(all(e));
        int r = 0, c = 0;
        ll ans = 0;
        per(i, 0, SZ(e)) {
            if (e[i].y >= m) {
                ans += 1LL * e[i].x * (r + 1);  
                c++;
            } 
            else {
                ans += 1LL * e[i].x * (c + 1);
                r++;
            }
        }
        return ans;
    }
};
