class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        verticalPieces = horizontalPieces = 1
        horizontalCut.sort()
        verticalCut.sort()
        cost = 0
        while horizontalCut or verticalCut:
            if horizontalCut and verticalCut:
                cut = 'v' if verticalCut[-1] > horizontalCut[-1] else 'h'
            elif verticalCut:
                cut = 'v'
            else:
                cut = 'h'
            if cut == 'v':
                cost += verticalCut.pop() * horizontalPieces
                verticalPieces += 1
            else:
                cost += horizontalCut.pop() * verticalPieces
                horizontalPieces += 1
        return cost
