class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& hor, vector<int>& ver) {
        int hp = 1;
        int vp = 1;
        sort(hor.rbegin(),hor.rend());
        sort(ver.rbegin(),ver.rend());
        int i = 0;
        int j = 0;

        long long cost = 0;
        while(i < n-1 or j < m-1){
            if(i == n-1){
                cost += 1ll * ver[j] * hp;
                vp++;
                j++;
            }
            else if(j == m-1){
                cost += 1ll * hor[i] * vp;
                hp++;
                i++;
            }
            else if(hor[i] > ver[j]){
                cost += 1ll * hor[i] * vp;
                hp++;
                i++;
            }
            else{
                cost += 1ll * ver[j] * hp;
                vp++;
                j++;
            }
        }
        return cost;
    }
};
