class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        long long res = 0;
        sort(hc.begin(),hc.end()); sort(vc.begin(),vc.end());
        for(int i=m-2, j=n-2, hcuts = 0, vcuts = 0; (i>=0 || j>=0);){
            if(i < 0 && j >= 0){ ++vcuts; res += (1LL*(hcuts+1)*vc[j--]); 
            continue;}
            if(i >=0 && j < 0){ ++hcuts; res += (1LL*(vcuts+1)*hc[i--]);
             continue;}
            if(hc[i] > vc[j]){
                ++hcuts; res += (1LL*(vcuts+1)*hc[i--]);
            }else{
                ++vcuts; res += (1LL*(hcuts+1)*vc[j--]);
            }
        }
        return res;
    }
};
