class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& H, vector<int>& V) {
        sort(H.begin(),H.end(),greater<int>());
        sort(V.begin(),V.end(),greater<int>());
        int h_pieces=0,v_pieces=0;
        int i=0,j=0;
        long long int ans=0;
        while(i<H.size() and j<V.size()){
            if(H[i]>=V[j]){
                ans+=H[i++]*1LL*(v_pieces+1);
                h_pieces++;
            }
            else{
                ans+=V[j++]*1LL*(h_pieces+1);
                v_pieces++;
            }
        }
        while(i<H.size())ans+=H[i++]*1LL*(v_pieces+1);
        while(j<V.size())ans+=V[j++]*1LL*(h_pieces+1);
        return ans;
    }
};
