class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        a=sorted([(i,1) for i in horizontalCut]+[(j,2) for j in verticalCut],reverse=True)
        col=1
        lie=1
        ans=0
        for x,y in a:
          if y==1:
            ans+=col*x
            lie+=1
          else:
            ans+=lie*x
            col+=1
        return ans
