/**
 * @param {number} m
 * @param {number} n
 * @param {number[]} horizontalCut
 * @param {number[]} verticalCut
 * @return {number}
 */
var minimumCost = function(m, n, horizontalCut, verticalCut) {
    horizontalCut.sort((a, b) => b - a);
    verticalCut.sort((a, b) => b - a);

    let horizontalPieces = 1;
    let verticalPieces = 1;

    let totalCost = 0;
    let i = 0, j = 0;

    while (i < horizontalCut.length || j < verticalCut.length) {
        if (j === verticalCut.length || horizontalCut[i] > verticalCut[j]) {
            totalCost += horizontalCut[i] * verticalPieces;
            horizontalPieces++;
            i++;
        } else {
            totalCost += verticalCut[j] * horizontalPieces;
            verticalPieces++;
            j++;
        }
    }

    return totalCost;
};
