bool comp(int &a, int &b){
    return (a > b);
}
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& x, vector<int>& y) {
        sort(x.begin(), x.end(), comp);
        sort(y.begin(), y.end(), comp);
        //algorithm
        int hori = 0, ver = 0; //no of cuts have done horizontally and vertically
        int i = 0, j = 0; 
        long long cost = 0;
        while(i < x.size() && j < y.size()){
            if(x[i] > y[j]){
                cost += (long long)(ver + 1) * (long long)x[i];
                hori++;
                i++;
            }
            else{
                cost += (long long)(hori + 1) * (long long)y[j];
                ver++;
                j++;
            }
        }
        if(i == x.size()){
            while(j < y.size()){
                cost += (long long)(hori + 1) * (long long)y[j];
                j++;
            }
        }
        if(j == y.size()){
            while(i < x.size()){
                cost += (long long)(ver + 1) * (long long)x[i];
                i++;
            }
        }
        return cost;
    }
};
