class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hz, vector<int>& ver) {

        sort(hz.begin(), hz.end(), greater<int>());
        sort(ver.begin(), ver.end(), greater<int>());

        long long h_seg = 1, v_seg = 1; 
        long long ans = 0;

        long long i = 0, j = 0;
        while (i < m-1 && j < n-1) {
            if (hz[i] > ver[j]) {
                ans += hz[i] * v_seg;
                h_seg++;
                i++;
            } else {
                ans += ver[j] * h_seg;
                v_seg++;
                j++;
            }
        }

        while (i < m-1) {
            ans += hz[i] * v_seg;
            h_seg++;
            i++;
        }

        while (j < n-1) {
            ans += ver[j] * h_seg;
            v_seg++;
            j++;
        }

        return ans;

    }
};
