class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        ans = sum(h) + sum(v)
        h.sort()
        pre_sum_h = [0]
        for c in h:
            pre_sum_h.append(pre_sum_h[-1] + c)

        for i in range(n - 1):
            idx = bisect.bisect(h, v[i])
            ans += v[i] * (len(h) - idx) + pre_sum_h[idx]
        return ans
