class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        if m < n:
            m, n = n, m
            horizontalCut, verticalCut = verticalCut, horizontalCut
        ls = []
        for h in sorted(horizontalCut):
            ls.append((h, 1))
        for v in sorted(verticalCut):
            ls.append((v, 0))
        ls.sort(reverse=True)
        # print(ls)
        cur_h, cur_v = 1, 1
        ans = 0
        for x, t in ls:
            if t == 1:
                ans += x * cur_v
                cur_h += 1
            else:
                ans += x * cur_h
                cur_v += 1
        return ans
