#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
        sort(verticalCut.begin(), verticalCut.end(), greater<int>());
        ll nh = horizontalCut.size(), nv = verticalCut.size();
        ll cnt{}, h{1}, v{1};
        ll i{}, j{};

        while(i < nh and j < nv){
            if(horizontalCut[i] >= verticalCut[j]){
                cnt += horizontalCut[i] * v;
                h++;
                i++;
            } 
            else{
                cnt += verticalCut[j] * h;
                v++;
                j++;
            }
        }
        while(i < nh){
            cnt += horizontalCut[i] * v;
            i++;
        }

        while(j < nv){
            cnt += verticalCut[j] * h;
            j++;
        }

        return cnt;
    }
};
