class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int,int>> p;

        for(int i=0; i<h.size(); i++){
            p.push_back({h[i],1});  

        }
        for(int i=0; i<v.size(); i++){
            p.push_back({v[i],0});
        }

        sort(p.begin(),p.end());
        reverse(p.begin(),p.end());

        long long res=0;
        int l=1,r=1; // l for hor, r for ver
        for(int i=0; i<p.size(); i++){
            if(p[i].second == 1){
                res += p[i].first*l ;
                r++;
            }
            else{
                res += p[i].first*r;
                l++;
            }
        }

        return res;
    }
};
