class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        multiset<pair<int, int>> cuts;
        for (auto h: horizontalCut) {
            cuts.emplace(-h, 0);
        }
        for (auto v: verticalCut) {
            cuts.emplace(-v, 1);
        }
        vector<int> already(2, 0);

        long long ans = 0;
        for (auto [neg_cost, type]: cuts) {
            long long cost = -neg_cost;
            ans += (already[1-type]+1) * cost;
            already[type]++;
        }
        return ans;

    }
};
