#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
         sort(h.begin(), h.end(), greater<int>());
        sort(v.begin(), v.end(), greater<int>());

        ll i = 0, j = 0;
        ll hCnt = 1, vCnt = 1;
        ll cost = 0;

        while(i<h.size() || j<v.size()){

            if(i>=h.size()){
                vCnt++;
                cost += (hCnt*v[j]); j++;
            }else if( j>= v.size()){
                hCnt++;
                cost += (vCnt * h[i]); i++;
            }else{
                if(h[i] > v[j]){
                    hCnt++;
                    cost += (vCnt * h[i]); i++;
                }else{
                    vCnt++;
                    cost += (hCnt*v[j]); j++;
                }
            }
        }
        return cost;
    }
};
