class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& y, vector<int>& x) {
        sort(x.begin(), x.end(), greater<int>()); 
        sort(y.begin(), y.end(), greater<int>());

        long long h = 1, v = 1; 
        long long cost = 0;

        int i = 0, j = 0;
        while (i<x.size() && j<y.size()) {
            if (x[i]>y[j]) {
                cost += x[i]*v;
                h++; 
                i++; 
            } else {
                cost += y[j]*h;
                v++; 
                j++; 
            }
        }

        while (i<x.size()) {
            cost+=x[i]*v;
            i++;
        }
        while (j<y.size()) {
            cost+=y[j]*h;
            j++;
        }

        return cost;
    }
};
