class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& ve) {
        vector<pair<long long,long long>>v;

        for(int i=0;i<h.size();i++) v.push_back({h[i],0});
        for(int i=0;i<ve.size();i++) v.push_back({ve[i],1});

        sort(v.begin(),v.end());

        long long ans=0,cm=m,cn=n;

        for(int i=v.size()-1;i>=0;i--){
            //cout<<v[i].first<<" ";
            if(v[i].second==0){
                ans+=v[i].first*(m-cm+1);
                cn--;
            } else{
                ans+=v[i].first*(n-cn+1);
                cm--;
            }
        }

        return ans;


    }
};
