class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int vc=1,hc=1;

        vector<pair<int,char>>cut;
        for(int i=0;i<verticalCut.size();i++){
            cut.push_back({verticalCut[i],'v'});
        }
        for(int i=0;i<horizontalCut.size();i++){
            cut.push_back({horizontalCut[i],'h'});
        }
        sort(cut.begin(),cut.end(),[](pair<int,char>& a,pair<int,char>& b){
            return a.first>b.first;
        });
        long long ans=0;
        for(int i=0;i<cut.size();i++){
            if(cut[i].second=='h'){
                ans+=cut[i].first*vc;
                hc++;
            }else{
                ans+=cut[i].first*hc;
                vc++;
            }
        }
        return ans;
    }
};
