class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        HORIZONTAL = 0
        VERTICAL = 1
        cuts = sorted([[hc, HORIZONTAL] for hc in horizontalCut] + [[vc, VERTICAL] for vc in verticalCut])[::-1]
        n_vPieces = 1
        n_hPieces = 1
        res = 0
        for cost, direction in cuts:
            if direction == HORIZONTAL:
                n_hPieces += 1
                res += cost * (n_vPieces)
            else:
                n_vPieces += 1
                res += cost * (n_hPieces)
        return res
