class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long ans = 0;
        vector<long long> hsum(m), vsum(n);
        sort(verticalCut.begin(), verticalCut.end());
        for (int i = 1; i < n; i++){
            vsum[i] = vsum[i - 1] + verticalCut[i - 1];
        }
        ans += vsum.back();

        for (int i : horizontalCut){
            int idx = int(lower_bound(verticalCut.begin(), verticalCut.end(), i) - verticalCut.begin());
            ans += vsum[idx];
            ans += (long long)i * (n - idx);
        }

        return ans;
    }
};
