class Solution {
    typedef long long int ll;
public:
    ll solvex(int m, int n, vector<int>& a, vector<int>& b) {
    sort(a.begin(), a.end(), greater<int>());
    sort(b.begin(), b.end(), greater<int>());

    ll h = 0, v = 0;
    ll h1 = 1, v1 = 1;
    ll ans = 0;

    while (h < a.size() && v < b.size()) {
        if (a[h] >= b[v]) {
            ans += a[h] * v1;
            h1++;
            h++;
        } else {
            ans += b[v] * h1;
            v1++;
            v++;
        }
    }

    if (h < a.size()) {
        for (int i = h; i < a.size(); ++i) {
            ans += a[i] * v1;
        }
    }

    if (v < b.size()) {
        for (int i = v; i < b.size(); ++i) {
            ans += b[i] * h1;
        }
    }

    return ans;
}
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
       ll ans=solvex(m,n,h,v);
        return ans;
    }
};
