using ll = long long;
#define all(x) x.begin(), x.end()

class Solution
{
public:
    ll minimumCost(int m, int n, vector<int> &hCut, vector<int> &vCut)
    {
        sort(all(hCut), greater<int>());
        sort(all(vCut), greater<int>());

        ll finalAnswer = 0, hp = 1, vp = 1, hx = 0, vx = 0;

        while (hx < m - 1 && vx < n - 1)
            if (hCut[hx] < vCut[vx])
                finalAnswer += (ll)vCut[vx++] * hp, vp++;
            else
                finalAnswer += (ll)hCut[hx++] * vp, hp++;

        while (hx < hCut.size())
            finalAnswer += (ll)hCut[hx++] * vp;

        while (vx < vCut.size())
            finalAnswer += (ll)vCut[vx++] * hp;

        return finalAnswer;
    }
};
