class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {

        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        long long totalCost = 0;
        int horizontalPieces = 1, verticalPieces = 1;
        int i = 0, j = 0;

        // Process the cuts in descending order of cost
        while (i < horizontalCut.size() && j < verticalCut.size()) {
            if (horizontalCut[i] >= verticalCut[j]) {
                totalCost += horizontalCut[i] * verticalPieces;
                ++horizontalPieces;
                ++i;
            } else {
                totalCost += verticalCut[j] * horizontalPieces;
                ++verticalPieces;
                ++j;
            }
        }

        // Process remaining horizontal cuts
        while (i < horizontalCut.size()) {
            totalCost += horizontalCut[i] * verticalPieces;
            ++horizontalPieces;
            ++i;
        }

        // Process remaining vertical cuts
        while (j < verticalCut.size()) {
            totalCost += verticalCut[j] * horizontalPieces;
            ++verticalPieces;
            ++j;
        }

        return totalCost;

    }
};
