class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<int>v1,v2;
        long long sh=0;
        long long sv=0;
        long long ans=0, cnth=1,cntv=1;
        for(int i=0;i<m-1;i++)
        {
            sh+=h[i];
        }
        vector<pair<long long,long long>>vv;

        for(int i=0;i<n-1;i++)
        {
            sv+=v[i];
        }

        for(int i=0;i<n-1;i++)
        {
            vv.push_back({v[i],1});
        }
         for(int i=0;i<m-1;i++)
        {
            vv.push_back({h[i],0});
        }int res=0;
        sort(vv.begin(),vv.end());
        for(int i=vv.size()-1;i>=0;i--)
        {
            if(vv[i].second==1)
            {
                cntv=cntv+1;
                int temp=vv[i].first*cnth;
                ans+=temp;
                res+=vv[i].first;
                sh=vv[i].first;

            }
            else
            {
                cnth=cnth+1;
                int temp=vv[i].first*cntv;
                ans+=temp;
                res+=vv[i].first;
                sv=vv[i].first;

            }
        }

        for(int i=0;i<vv.size();i++)
        {
          res+=vv[i].first;  
        }
        return ans;
    }
};
